import React from 'react';
import styled from 'styled-components';
import { FiExternalLink, FiBookmark, FiMapPin, FiDollarSign } from 'react-icons/fi';

const JobsContainer = styled.div`
  max-height: 400px;
  overflow-y: auto;
`;

const JobItem = styled.div`
  padding: 15px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  
  &:last-child {
    border-bottom: none;
  }
`;

const JobTitle = styled.h4`
  margin: 0 0 8px 0;
  font-size: 14px;
  line-height: 1.4;
  color: white;
`;

const JobCompany = styled.div`
  font-size: 12px;
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 8px;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const JobMeta = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 11px;
  color: rgba(255, 255, 255, 0.6);
  margin-bottom: 10px;
`;

const JobActions = styled.div`
  display: flex;
  gap: 10px;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  color: rgba(255, 255, 255, 0.6);
  cursor: pointer;
  padding: 4px;
  border-radius: 4px;
  transition: all 0.2s ease;

  &:hover {
    color: white;
    background: rgba(255, 255, 255, 0.1);
  }
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 40px;
  color: rgba(255, 255, 255, 0.6);
`;

function JobsSection({ data, loading, onUserAction }) {
  if (loading) {
    return <LoadingMessage>Loading job opportunities...</LoadingMessage>;
  }

  if (!data || !data.jobs) {
    return <LoadingMessage>No jobs available at the moment.</LoadingMessage>;
  }

  const handleAction = (action, job) => {
    onUserAction(action, job.id, 'jobs');
  };

  return (
    <JobsContainer>
      {data.jobs.slice(0, 5).map((job, index) => (
        <JobItem key={job.id || index}>
          <JobTitle>{job.title}</JobTitle>
          <JobCompany>
            <span>{job.company}</span>
            {job.location && (
              <>
                <FiMapPin />
                <span>{job.location}</span>
              </>
            )}
          </JobCompany>
          <JobMeta>
            <div>
              {job.salary && (
                <span>
                  <FiDollarSign />
                  {job.salary}
                </span>
              )}
            </div>
            <span>{new Date(job.posted_at).toLocaleDateString()}</span>
          </JobMeta>
          <JobActions>
            <ActionButton 
              onClick={() => handleAction('bookmark', job)}
              title="Save Job"
            >
              <FiBookmark />
            </ActionButton>
            <ActionButton 
              onClick={() => {
                window.open(job.url, '_blank');
                handleAction('click', job);
              }}
              title="Apply"
            >
              <FiExternalLink />
            </ActionButton>
          </JobActions>
        </JobItem>
      ))}
    </JobsContainer>
  );
}

export default JobsSection;
