import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const Container = styled.div`
  max-width: 800px;
  margin: 2rem auto;
  padding: 2rem;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const Title = styled.h2`
  color: white;
  text-align: center;
  margin-bottom: 2rem;
`;

const Section = styled.div`
  margin-bottom: 2rem;
`;

const SectionTitle = styled.h3`
  color: white;
  margin-bottom: 1rem;
`;

const PreferenceGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
  gap: 1rem;
  margin-bottom: 1.5rem;
`;

const PreferenceChip = styled.div`
  background: ${props => props.selected ? 'rgba(255, 255, 255, 0.3)' : 'rgba(255, 255, 255, 0.1)'};
  border: 2px solid ${props => props.selected ? 'rgba(255, 255, 255, 0.6)' : 'rgba(255, 255, 255, 0.2)'};
  border-radius: 12px;
  padding: 1rem;
  text-align: center;
  cursor: pointer;
  transition: all 0.3s ease;
  color: white;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
    border-color: rgba(255, 255, 255, 0.4);
  }
`;

const Button = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  padding: 0.75rem 1.5rem;
  border-radius: 25px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  display: block;
  margin: 2rem auto 0;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

const foodOptions = [
  { id: 'italian', name: 'Italian', emoji: 'üçï' },
  { id: 'mexican', name: 'Mexican', emoji: 'üåÆ' },
  { id: 'indian', name: 'Indian', emoji: 'üçõ' },
  { id: 'chinese', name: 'Chinese', emoji: 'ü•°' },
  { id: 'japanese', name: 'Japanese', emoji: 'üç£' },
  { id: 'american', name: 'American', emoji: 'üçî' },
  { id: 'thai', name: 'Thai', emoji: 'üçú' },
  { id: 'mediterranean', name: 'Mediterranean', emoji: 'ü•ô' },
];

const dietaryOptions = [
  { id: 'vegetarian', name: 'Vegetarian', emoji: 'ü•¶' },
  { id: 'vegan', name: 'Vegan', emoji: 'üå±' },
  { id: 'gluten-free', name: 'Gluten-Free', emoji: 'üåæ' },
  { id: 'dairy-free', name: 'Dairy-Free', emoji: 'ü•õ' },
  { id: 'nut-free', name: 'Nut-Free', emoji: 'ü•ú' },
];

const movieGenres = [
  { id: 'action', name: 'Action', emoji: 'üí•' },
  { id: 'comedy', name: 'Comedy', emoji: 'üòÇ' },
  { id: 'drama', name: 'Drama', emoji: 'üé≠' },
  { id: 'sci-fi', name: 'Sci-Fi', emoji: 'üöÄ' },
  { id: 'horror', name: 'Horror', emoji: 'üëª' },
  { id: 'romance', name: 'Romance', emoji: '‚ù§Ô∏è' },
  { id: 'documentary', name: 'Documentary', emoji: 'üé•' },
  { id: 'animation', name: 'Animation', emoji: 'ü¶Ñ' },
];

const newsCategories = [
  { id: 'technology', name: 'Technology', emoji: 'üíª' },
  { id: 'sports', name: 'Sports', emoji: '‚öΩ' },
  { id: 'business', name: 'Business', emoji: 'üíº' },
  { id: 'entertainment', name: 'Entertainment', emoji: 'üé≠' },
  { id: 'health', name: 'Health', emoji: 'üè•' },
  { id: 'science', name: 'Science', emoji: 'üî¨' },
  { id: 'politics', name: 'Politics', emoji: 'üèõÔ∏è' },
  { id: 'environment', name: 'Environment', emoji: 'üåç' },
];

const InitialPreferences = ({ onComplete }) => {
  const [preferences, setPreferences] = useState({
    food: { cuisines: [], dietary: [] },
    movies: { genres: [] },
    news: { categories: [] }
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const togglePreference = (category, subcategory, optionId) => {
    setPreferences(prev => {
      const current = prev[category]?.[subcategory] || [];
      const updated = current.includes(optionId)
        ? current.filter(id => id !== optionId)
        : [...current, optionId];

      return {
        ...prev,
        [category]: {
          ...prev[category],
          [subcategory]: updated
        }
      };
    });  
  };

  const handleSave = async () => {
    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('auth_token');
      if (!token) {
        throw new Error('Not authenticated');
      }

      const response = await fetch('http://localhost:5000/api/preferences', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ...preferences,
          is_initial_setup: true
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'Failed to save preferences');
      }

      onComplete();
      navigate('/dashboard');

    } catch (err) {
      console.error('Error saving preferences:', err);
      setError(err.message || 'Failed to save preferences. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <Title>Welcome! Let's set up your preferences</Title>

      {error && <div style={{ color: 'red', marginBottom: '1rem', textAlign: 'center' }}>{error}</div>}

      <Section>
        <SectionTitle>üçΩÔ∏è Food Preferences</SectionTitle>

        <div style={{ marginBottom: '1.5rem' }}>
          <h4 style={{ color: 'white', marginBottom: '0.75rem' }}>Favorite Cuisines</h4>
          <PreferenceGrid>
            {foodOptions.map(option => (
              <PreferenceChip
                key={option.id}
                selected={preferences.food?.cuisines?.includes(option.id)}
                onClick={() => togglePreference('food', 'cuisines', option.id)}
              >
                <div style={{ fontSize: '1.5rem' }}>{option.emoji}</div>
                <div>{option.name}</div>
              </PreferenceChip>
            ))}
          </PreferenceGrid>
        </div>

        <div>
          <h4 style={{ color: 'white', marginBottom: '0.75rem' }}>Dietary Restrictions</h4>
          <PreferenceGrid>
            {dietaryOptions.map(option => (
              <PreferenceChip
                key={option.id}
                selected={preferences.food?.dietary?.includes(option.id)}
                onClick={() => togglePreference('food', 'dietary', option.id)}
              >
                <div style={{ fontSize: '1.5rem' }}>{option.emoji}</div>
                <div>{option.name}</div>
              </PreferenceChip>
            ))}
          </PreferenceGrid>
        </div>
      </Section>

      <Section>
        <SectionTitle>üé¨ Movie Preferences</SectionTitle>
        <PreferenceGrid>
          {movieGenres.map(option => (
            <PreferenceChip
              key={option.id}
              selected={preferences.movies?.genres?.includes(option.id)}
              onClick={() => togglePreference('movies', 'genres', option.id)}
            >
              <div style={{ fontSize: '1.5rem' }}>{option.emoji}</div>
              <div>{option.name}</div>
            </PreferenceChip>
          ))}
        </PreferenceGrid>
      </Section>

      <Section>
        <SectionTitle>üì∞ News Preferences</SectionTitle>
        <PreferenceGrid>
          {newsCategories.map(option => (
            <PreferenceChip
              key={option.id}
              selected={preferences.news?.categories?.includes(option.id)}
              onClick={() => togglePreference('news', 'categories', option.id)}
            >
              <div style={{ fontSize: '1.5rem' }}>{option.emoji}</div>
              <div>{option.name}</div>
            </PreferenceChip>
          ))}
        </PreferenceGrid>
      </Section>

      <Button 
        onClick={handleSave}
        disabled={loading}
      >
        {loading ? 'Saving...' : 'Save Preferences & Continue'}
      </Button>
    </Container>
  );
};

export default InitialPreferences;
