function Settings({ user }) {
  const [preferences, setPreferences] = useState({
    food: { cuisines: [], dietary: [] },
    movies: { genres: [], languages: [] }
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadPreferences();
  }, []);

  const loadPreferences = async () => {
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch('http://localhost:5000/api/preferences', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setPreferences(data.preferences || {
          food: { cuisines: [], dietary: [] },
          movies: { genres: [], languages: [] }
        });
      }
    } catch (error) {
      console.error('Error loading preferences:', error);
    }
  };

  const togglePreference = (category, subcategory, optionId) => {
    setPreferences(prev => {
      const current = prev[category]?.[subcategory] || [];
      const updated = current.includes(optionId)
        ? current.filter(id => id !== optionId)
        : [...current, optionId];

      return {
        ...prev,
        [category]: {
          ...prev[category],
          [subcategory]: updated
        }
      };
    });
  };

  const savePreferences = async () => {
    setLoading(true);
    setSuccess('');

    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch('http://localhost:5000/api/preferences', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(preferences)
      });

      if (response.ok) {
        setSuccess('Preferences updated successfully!');
        setTimeout(() => setSuccess(''), 3000);
      }
    } catch (error) {
      console.error('Error saving preferences:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <SettingsContainer>
      <SettingsCard>
        <Title>
          <FiSettings />
          Account Settings
        </Title>

        {success && <SuccessMessage>{success}</SuccessMessage>}

        <Section>
          <SectionTitle>
            <FiUser />
            Profile Information
          </SectionTitle>
          <UserInfoGrid>
            <InfoCard>
              <InfoLabel>
                <FiUser />
                Full Name
              </InfoLabel>
              <InfoValue>{user?.name || 'Not provided'}</InfoValue>
            </InfoCard>
            <InfoCard>
              <InfoLabel>
                <FiMail />
                Email Address
              </InfoLabel>
              <InfoValue>{user?.email || 'Not provided'}</InfoValue>
            </InfoCard>
          </UserInfoGrid>
        </Section>

        <Section>
          <SectionTitle>
            🍽️ Food Preferences
          </SectionTitle>

          <div style={{ marginBottom: '25px' }}>
            <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '1.1rem' }}>
              Favorite Cuisines
            </h4>
            <PreferenceGrid>
              {foodOptions.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.food?.cuisines?.includes(option.id)}
                  onClick={() => togglePreference('food', 'cuisines', option.id)}
                >
                  {option.name}
                </PreferenceChip>
              ))}
            </PreferenceGrid>
          </div>

          <div>
            <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '1.1rem' }}>
              Dietary Restrictions
            </h4>
            <PreferenceGrid>
              {dietaryOptions.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.food?.dietary?.includes(option.id)}
                  onClick={() => togglePreference('food', 'dietary', option.id)}
                >
                  {option.name}
                </PreferenceChip>
              ))}
            </PreferenceGrid>
          </div>
        </Section>

        <Section>
          <SectionTitle>
            🎬 Movie Preferences
          </SectionTitle>

          <div style={{ marginBottom: '25px' }}>
            <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '1.1rem' }}>
              Favorite Genres
            </h4>
            <PreferenceGrid>
              {movieGenres.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.movies?.genres?.includes(option.id)}
                  onClick={() => togglePreference('movies', 'genres', option.id)}
                >
                  {option.name}
                </PreferenceChip>
              ))}
            </PreferenceGrid>
          </div>

          <div>
            <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '1.1rem' }}>
              Preferred Languages
            </h4>
            <PreferenceGrid>
              {movieLanguages.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.movies?.languages?.includes(option.id)}
                  onClick={() => togglePreference('movies', 'languages', option.id)}
                >
                  {option.name}
                </PreferenceChip>
              ))}
            </PreferenceGrid>
          </div>
        </Section>

        <SaveButton onClick={savePreferences} disabled={loading}>
          <FiSave />
          {loading ? 'Saving...' : 'Save Preferences'}
        </SaveButton>
      </SettingsCard>
    </SettingsContainer>
  );
}

