import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import Auth from './components/Auth';
import InitialPreferences from './components/InitialPreferences';
import Settings from './components/Settings';
import WeatherDashboard from './components/WeatherDashboard';
import CryptoTracker from './components/CryptoTracker';
import RecipeFinder from './components/RecipeFinder';
import Header from './components/Header';
import './App.css';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
`;

const MainContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

function App() {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [needsPreferences, setNeedsPreferences] = useState(false);

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      const token = localStorage.getItem('auth_token');
      const savedUser = localStorage.getItem('user_data');

      if (!token || !savedUser) {
        setIsLoading(false);
        return;
      }

      try {
        // Verify token with backend and get current user info
        const response = await fetch('http://localhost:5000/api/auth/me', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Invalid token');
        }

        const userData = await response.json();
        setUser(userData);
        setIsAuthenticated(true);

        // Check if user needs to set preferences
        const prefsResponse = await fetch('http://localhost:5000/api/preferences', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (prefsResponse.ok) {
          const prefsData = await prefsResponse.json();
          setNeedsPreferences(!prefsData.has_set_preferences);
        }
      } catch (error) {
        console.error('Error checking auth status:', error);
        // Clear invalid auth data
        localStorage.removeItem('auth_token');
        localStorage.removeItem('user_data');
        setIsAuthenticated(false);
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      setIsAuthenticated(false);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAuthSuccess = (userData, token) => {
    setUser(userData);
    setIsAuthenticated(true);
    localStorage.setItem('auth_token', token);
    localStorage.setItem('user_data', JSON.stringify(userData));
    setNeedsPreferences(!(userData.has_set_preferences || false));
  };

  const handlePreferencesComplete = () => {
    setNeedsPreferences(false);
    // Update user data in state and localStorage
    const updatedUser = { ...user, has_set_preferences: true };
    setUser(updatedUser);
    localStorage.setItem('user_data', JSON.stringify(updatedUser));
  };

  const handleLogout = () => {
    localStorage.removeItem('auth_token');
    localStorage.removeItem('user_data');
    setUser(null);
    setIsAuthenticated(false);
    setNeedsPreferences(false);
  };

  if (isLoading) {
    return (
      <AppContainer>
        <MainContent>
          <div style={{ textAlign: 'center', padding: '50px', color: 'white' }}>
            <h2>Loading your personalized dashboard...</h2>
          </div>
        </MainContent>
      </AppContainer>
    );
  }

  return (
    <Router>
      <AppContainer>
        {isAuthenticated && !needsPreferences && <Header user={user} onLogout={handleLogout} />}
        <MainContent>
          <Routes>
            {needsPreferences ? (
              <Route path="/*" element={<InitialPreferences onComplete={handlePreferencesComplete} />} />
            ) : (
              <>
                <Route 
                  path="/" 
                  element={user ? <Dashboard user={user} /> : <Navigate to="/login" />} 
                />
                <Route path="/settings" element={<Settings user={user} />} />
                <Route path="/weather" element={<WeatherDashboard />} />
                <Route path="/crypto" element={<CryptoTracker />} />
                <Route path="/recipes" element={<RecipeFinder />} />
                <Route path="*" element={<Navigate to="/" />} />
              </>
            )}
            <Route 
              path="/login" 
              element={
                isAuthenticated ? 
                <Navigate to="/" /> : 
                <Auth onAuthSuccess={handleAuthSuccess} />
              } 
            />
            <Route 
              path="/register" 
              element={
                isAuthenticated ? 
                <Navigate to="/" /> : 
                <Auth isRegister onAuthSuccess={handleAuthSuccess} />
              } 
            />
          </Routes>
        </MainContent>
      </AppContainer>
    </Router>
  );
}

export default App;
