import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FiSettings, FiSave, FiUser, FiMail } from 'react-icons/fi';

const SettingsContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
`;

const SettingsCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 30px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  margin-bottom: 20px;
`;

const Title = styled.h2`
  color: white;
  margin-bottom: 30px;
  font-size: 2rem;
  font-weight: 300;
  display: flex;
  align-items: center;
  gap: 15px;
`;

const Section = styled.div`
  margin-bottom: 40px;
`;

const SectionTitle = styled.h3`
  color: white;
  margin-bottom: 20px;
  font-size: 1.3rem;
  display: flex;
  align-items: center;
  gap: 10px;
`;

const UserInfoGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;
  margin-bottom: 30px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const InfoCard = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  padding: 20px;
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const InfoLabel = styled.div`
  color: rgba(255, 255, 255, 0.7);
  font-size: 14px;
  margin-bottom: 8px;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const InfoValue = styled.div`
  color: white;
  font-size: 16px;
  font-weight: 500;
`;

const PreferenceGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 12px;
  margin-bottom: 20px;
`;

const PreferenceChip = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border: 2px solid ${props => props.selected ? 'rgba(255, 255, 255, 0.6)' : 'rgba(255, 255, 255, 0.2)'};
  border-radius: 25px;
  padding: 10px 15px;
  text-align: center;
  cursor: pointer;
  transition: all 0.3s ease;
  color: white;
  font-size: 14px;

  &:hover {
    background: rgba(255, 255, 255, 0.15);
    border-color: rgba(255, 255, 255, 0.4);
  }

  ${props => props.selected && `
    background: rgba(255, 255, 255, 0.2);
    transform: translateY(-1px);
  `}
`;

const SaveButton = styled.button`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border: none;
  border-radius: 12px;
  padding: 15px 30px;
  color: white;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 10px;
  margin-top: 20px;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const SuccessMessage = styled.div`
  background: rgba(0, 255, 0, 0.1);
  border: 1px solid rgba(0, 255, 0, 0.3);
  border-radius: 8px;
  padding: 12px;
  color: #51cf66;
  text-align: center;
  margin-bottom: 20px;
`;

const foodOptions = [
  { id: 'italian', name: 'Italian' },
  { id: 'chinese', name: 'Chinese' },
  { id: 'indian', name: 'Indian' },
  { id: 'mexican', name: 'Mexican' },
  { id: 'japanese', name: 'Japanese' },
  { id: 'american', name: 'American' },
  { id: 'thai', name: 'Thai' },
  { id: 'mediterranean', name: 'Mediterranean' }
];

const dietaryOptions = [
  { id: 'vegetarian', name: 'Vegetarian' },
  { id: 'vegan', name: 'Vegan' },
  { id: 'gluten-free', name: 'Gluten Free' },
  { id: 'keto', name: 'Keto' },
  { id: 'halal', name: 'Halal' },
  { id: 'kosher', name: 'Kosher' }
];

const movieGenres = [
  { id: 'action', name: 'Action' },
  { id: 'comedy', name: 'Comedy' },
  { id: 'drama', name: 'Drama' },
  { id: 'horror', name: 'Horror' },
  { id: 'romance', name: 'Romance' },
  { id: 'sci-fi', name: 'Sci-Fi' },
  { id: 'thriller', name: 'Thriller' },
  { id: 'documentary', name: 'Documentary' }
];

const movieLanguages = [
  { id: 'english', name: 'English' },
  { id: 'spanish', name: 'Spanish' },
  { id: 'french', name: 'French' },
  { id: 'hindi', name: 'Hindi' },
  { id: 'korean', name: 'Korean' },
  { id: 'japanese', name: 'Japanese' }
];

function Settings({ user }) {
  const [preferences, setPreferences] = useState({
    food: { cuisines: [], dietary: [] },
    movies: { genres: [], languages: [] }
  });
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadPreferences();
  }, []);

  const loadPreferences = async () => {
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch('http://localhost:5000/api/preferences', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setPreferences(data.preferences || {
          food: { cuisines: [], dietary: [] },
          movies: { genres: [], languages: [] }
        });
      }
    } catch (error) {
      console.error('Error loading preferences:', error);
    }
  };

  const togglePreference = (category, subcategory, optionId) => {
    setPreferences(prev => {
      const current = prev[category]?.[subcategory] || [];
      const updated = current.includes(optionId)
        ? current.filter(id => id !== optionId)
        : [...current, optionId];

      return {
        ...prev,
        [category]: {
          ...prev[category],
          [subcategory]: updated
        }
      };
    });
  };

  const savePreferences = async () => {
    setLoading(true);
    setSuccess('');

    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch('http://localhost:5000/api/preferences', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(preferences)
      });

      if (response.ok) {
        setSuccess('Preferences updated successfully!');
        setTimeout(() => setSuccess(''), 3000);
      }
    } catch (error) {
      console.error('Error saving preferences:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <SettingsContainer>
      <SettingsCard>
        <Title>
          <FiSettings />
          Account Settings
        </Title>

        {success && <SuccessMessage>{success}</SuccessMessage>}

        <Section>
          <SectionTitle>
            <FiUser />
            Profile Information
          </SectionTitle>
          <UserInfoGrid>
            <InfoCard>
              <InfoLabel>
                <FiUser />
                Full Name
              </InfoLabel>
              <InfoValue>{user?.name || 'Not provided'}</InfoValue>
            </InfoCard>
            <InfoCard>
              <InfoLabel>
                <FiMail />
                Email Address
              </InfoLabel>
              <InfoValue>{user?.email || 'Not provided'}</InfoValue>
            </InfoCard>
          </UserInfoGrid>
        </Section>

        <Section>
          <SectionTitle>
            üçΩÔ∏è Food Preferences
          </SectionTitle>

          <div style={{ marginBottom: '25px' }}>
            <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '1.1rem' }}>
              Favorite Cuisines
            </h4>
            <PreferenceGrid>
              {foodOptions.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.food?.cuisines?.includes(option.id)}
                  onClick={() => togglePreference('food', 'cuisines', option.id)}
                >
                  {option.name}
                </PreferenceChip>
              ))}
            </PreferenceGrid>
          </div>

          <div>
            <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '1.1rem' }}>
              Dietary Restrictions
            </h4>
            <PreferenceGrid>
              {dietaryOptions.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.food?.dietary?.includes(option.id)}
                  onClick={() => togglePreference('food', 'dietary', option.id)}
                >
                  {option.name}
                </PreferenceChip>
              ))}
            </PreferenceGrid>
          </div>
        </Section>

        <Section>
          <SectionTitle>
            üé¨ Movie Preferences
          </SectionTitle>

          <div style={{ marginBottom: '25px' }}>
            <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '1.1rem' }}>
              Favorite Genres
            </h4>
            <PreferenceGrid>
              {movieGenres.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.movies?.genres?.includes(option.id)}
                  onClick={() => togglePreference('movies', 'genres', option.id)}
                >
                  {option.name}
                </PreferenceChip>
              ))}
            </PreferenceGrid>
          </div>

          <div>
            <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '1.1rem' }}>
              Preferred Languages
            </h4>
            <PreferenceGrid>
              {movieLanguages.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.movies?.languages?.includes(option.id)}
                  onClick={() => togglePreference('movies', 'languages', option.id)}
                >
                  {option.name}
                </PreferenceChip>
              ))}
            </PreferenceGrid>
          </div>
        </Section>

        <SaveButton onClick={savePreferences} disabled={loading}>
          <FiSave />
          {loading ? 'Saving...' : 'Save Preferences'}
        </SaveButton>
      </SettingsCard>
    </SettingsContainer>
  );
}

export default Settings;
