            </h4>
            <PreferenceGrid>
              {jobCategories.map(option => (
                <PreferenceChip
                  key={option.id}
                  selected={preferences.jobs?.categories?.includes(option.id)}
                  onClick={() => togglePreference('jobs', 'categories', option.id)}
