function PreferencesSetup({ user, onComplete }) {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [preferences, setPreferences] = useState({
    food: {
      cuisines: [],
      dietary: []
    },
    movies: {
      genres: [],
      languages: []
    },
    news: {
      categories: []
    },
    youtube: {
      categories: []
    },
    deals: {
      categories: []
    },
    jobs: {
      categories: []
    }
  });

  const toggleOption = (category, subcategory, optionId) => {
    setPreferences(prev => {
      const current = prev[category][subcategory];
      const updated = current.includes(optionId)
        ? current.filter(id => id !== optionId)
        : [...current, optionId];

      return {
        ...prev,
        [category]: {
          ...prev[category],
          [subcategory]: updated
        }
      };
    });
  };

  const handleNext = () => {
    if (step < 6) {
      setStep(step + 1);
    } else {
      handleComplete();
    }
  };

  const handleComplete = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch('http://localhost:5000/api/preferences', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(preferences)
      });

      if (response.ok) {
        onComplete(preferences);
      } else {
        if (onComplete) {
          onComplete(preferences);
        }
      }
      // Redirect to dashboard after saving preferences
      navigate('/dashboard');
    } catch (error) {
      console.error('Error saving preferences:', error);
      alert('Failed to save preferences. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const canProceed = () => {
    if (step === 1) {
      return preferences.food.cuisines.length > 0;
    }
    return preferences.movies.genres.length > 0;
  };

  return (
    <SetupContainer>
      <SetupCard>
        <Title>Let's Personalize Your Experience</Title>
        <Subtitle>
          Tell us about your preferences so we can recommend the best content for you
        </Subtitle>

        <StepIndicator>
          <Step active={step >= 1}>
            <StepNumber active={step >= 1}>1</StepNumber>
            Food Preferences
          </Step>
          <Step active={step >= 2}>
            <StepNumber active={step >= 2}>2</StepNumber>
            Movie Preferences
          </Step>
        </StepIndicator>

        {step === 1 && (
          <>
            <CategorySection>
              <CategoryTitle>
                <CategoryIcon>🍽️</CategoryIcon>
                What cuisines do you enjoy?
              </CategoryTitle>
              <OptionsGrid>
                {foodOptions.map(option => (
                  <OptionCard
                    key={option.id}
                    selected={preferences.food.cuisines.includes(option.id)}
                    onClick={() => toggleOption('food', 'cuisines', option.id)}
                  >
                    <OptionEmoji>{option.emoji}</OptionEmoji>
                    <OptionText>{option.name}</OptionText>
                  </OptionCard>
                ))}
              </OptionsGrid>
            </CategorySection>

            <CategorySection>
              <CategoryTitle>
                <CategoryIcon>🥗</CategoryIcon>
                Any dietary preferences?
              </CategoryTitle>
              <OptionsGrid>
                {dietaryOptions.map(option => (
                  <OptionCard
                    key={option.id}
                    selected={preferences.food.dietary.includes(option.id)}
                    onClick={() => toggleOption('food', 'dietary', option.id)}
                  >
                    <OptionEmoji>{option.emoji}</OptionEmoji>
                    <OptionText>{option.name}</OptionText>
                  </OptionCard>
                ))}
              </OptionsGrid>
            </CategorySection>
          </>
        )}

        {step === 2 && (
          <>
            <CategorySection>
              <CategoryTitle>
                <CategoryIcon>🎬</CategoryIcon>
                What movie genres do you like?
              </CategoryTitle>
              <OptionsGrid>
                {movieGenres.map(option => (
                  <OptionCard
                    key={option.id}
                    selected={preferences.movies.genres.includes(option.id)}
                    onClick={() => toggleOption('movies', 'genres', option.id)}
                  >
                    <OptionEmoji>{option.emoji}</OptionEmoji>
                    <OptionText>{option.name}</OptionText>
                  </OptionCard>
                ))}
              </OptionsGrid>
            </CategorySection>

            <CategorySection>
              <CategoryTitle>
                <CategoryIcon>🌍</CategoryIcon>
                Preferred languages?
              </CategoryTitle>
              <OptionsGrid>
                {movieLanguages.map(option => (
                  <OptionCard
                    key={option.id}
                    selected={preferences.movies.languages.includes(option.id)}
                    onClick={() => toggleOption('movies', 'languages', option.id)}
                  >
                    <OptionEmoji>{option.emoji}</OptionEmoji>
                    <OptionText>{option.name}</OptionText>
                  </OptionCard>
                ))}
              </OptionsGrid>
            </CategorySection>
          </>
        )}

        <ButtonContainer>
          <Button onClick={() => step > 1 && setStep(step - 1)} disabled={step === 1}>
            Back
          </Button>
          <Button 
            primary 
            onClick={handleNext} 
            disabled={!canProceed() || loading}
          >
            {loading ? 'Saving...' : (step === 2 ? 'Complete Setup' : 'Next')}
            {!loading && <FiArrowRight />}
          </Button>
        </ButtonContainer>
      </SetupCard>
    </SetupContainer>
  );
}

