import React from 'react';
import styled from 'styled-components';
import { FiExternalLink, FiBookmark, FiPlay } from 'react-icons/fi';

const VideosContainer = styled.div`
  max-height: 400px;
  overflow-y: auto;
`;

const VideoItem = styled.div`
  display: flex;
  gap: 15px;
  padding: 15px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  
  &:last-child {
    border-bottom: none;
  }
`;

const VideoThumbnail = styled.div`
  position: relative;
  width: 80px;
  height: 60px;
  border-radius: 8px;
  background: rgba(255, 255, 255, 0.1);
  overflow: hidden;
`;

const ThumbnailImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const PlayButton = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: white;
  font-size: 20px;
`;

const VideoContent = styled.div`
  flex: 1;
`;

const VideoTitle = styled.h4`
  margin: 0 0 8px 0;
  font-size: 14px;
  line-height: 1.4;
  color: white;
`;

const VideoChannel = styled.div`
  font-size: 12px;
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 8px;
`;

const VideoMeta = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 11px;
  color: rgba(255, 255, 255, 0.6);
`;

const VideoActions = styled.div`
  display: flex;
  gap: 10px;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  color: rgba(255, 255, 255, 0.6);
  cursor: pointer;
  padding: 4px;
  border-radius: 4px;
  transition: all 0.2s ease;

  &:hover {
    color: white;
    background: rgba(255, 255, 255, 0.1);
  }
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 40px;
  color: rgba(255, 255, 255, 0.6);
`;

function VideosSection({ data, loading, onUserAction }) {
  if (loading) {
    return <LoadingMessage>Loading trending videos...</LoadingMessage>;
  }

  if (!data || !data.videos) {
    return <LoadingMessage>No videos available at the moment.</LoadingMessage>;
  }

  // Check if this is static data
  const isStaticData = data.error === "STATIC DATA - No YouTube API Key Found" || data.videos.some(video => video.is_static);

  const handleAction = (action, video) => {
    onUserAction(action, video.id, 'videos');
  };

  return (
    <VideosContainer>
      {isStaticData && (
        <div style={{ 
          background: 'rgba(255, 107, 107, 0.1)', 
          border: '1px solid rgba(255, 107, 107, 0.2)', 
          borderRadius: '8px', 
          padding: '15px', 
          marginBottom: '15px', 
          color: '#ff6b6b',
          fontSize: '14px'
        }}>
          ⚠️ <strong>STATIC DATA WARNING:</strong> {data.message || 'This is mock data. Add YOUTUBE_API_KEY to env.local for real videos.'}
          <br />
          <small>Get API key: <a href="https://console.developers.google.com/" target="_blank" rel="noopener noreferrer" style={{color: '#4fc3f7'}}>https://console.developers.google.com/</a></small>
        </div>
      )}
      {data.videos.slice(0, 5).map((video, index) => (
        <VideoItem key={video.id || index}>
          <VideoThumbnail>
            <ThumbnailImage 
              src={video.thumbnail || '/placeholder-video.jpg'} 
              alt={video.title}
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
            <PlayButton>
              <FiPlay />
            </PlayButton>
          </VideoThumbnail>
          <VideoContent>
            <VideoTitle>{video.title}</VideoTitle>
            <VideoChannel>{video.channel}</VideoChannel>
            <VideoMeta>
              <div>
                {video.views && <span>{video.views.toLocaleString()} views</span>}
                {video.duration && <span> • {video.duration}</span>}
              </div>
              <VideoActions>
                <ActionButton 
                  onClick={() => handleAction('bookmark', video)}
                  title="Save Video"
                >
                  <FiBookmark />
                </ActionButton>
                <ActionButton 
                  onClick={() => {
                    if (video.url && video.url !== 'https://youtube.com/watch?v=demo1') {
                      window.open(video.url, '_blank', 'noopener,noreferrer');
                    } else {
                      // Handle mock data - redirect to YouTube
                      window.open('https://youtube.com', '_blank', 'noopener,noreferrer');
                    }
                    handleAction('click', video);
                  }}
                  title="Watch"
                >
                  <FiExternalLink />
                </ActionButton>
              </VideoActions>
            </VideoMeta>
          </VideoContent>
        </VideoItem>
      ))}
    </VideosContainer>
  );
}

export default VideosSection;
